import React, { useState, useEffect } from 'react';
import ScheduleMeetingForm from './components/ScheduleMeetingForm';
import MeetingList from './components/MeetingList';
import { getMeetings } from './services/api';
import './App.css';

function App() {
  const [meetings, setMeetings] = useState([]);

  // Function to fetch meetings from the server
  const fetchMeetings = async () => {
    try {
      const response = await getMeetings();
      setMeetings(response.data);
    } catch (error) {
      console.error("Error fetching meetings:", error);
    }
  };

  // Fetch meetings when the component mounts
  useEffect(() => {
    fetchMeetings();
  }, []);

  // Function to be called after a new meeting is created
  const handleMeetingCreated = () => {
    fetchMeetings(); // Re-fetch the list of meetings
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="text-3xl font-bold mb-6">Meeting Scheduler</h1>
      </header>
      <main className="App-main">
        <div className="flex flex-col items-center">
          {/* Pass the handleMeetingCreated function as a prop */}
          <ScheduleMeetingForm onMeetingCreated={handleMeetingCreated} />
          <div className="mt-8 w-full max-w-2xl">
            <MeetingList meetings={meetings} />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
